mapper.update:
update setmeal
category_id = #{categoryId}, name = #{name}, price = #{price}, status = #{status}, image = #{image}, description = #{description}, update_time = #{updateTime}, update_user = #{updateUser}
where id = #{id}

mapper.delete:
delete from setmeal
where id in

    #{id}

mapper.select:
select d.name, d.description, d.image,sd.copies from setmeal_dish sd
left join dish d on sd.dish_id = d.id
where setmeal_id = #{setmealId}

select s.*, c.name categoryName  from setmeal s
left join category c on s.category_id = c.id


        and s.category_id = #{categoryId}


        and s.name like concat('%',#{name},'%')


        and s.status = #{status}


order by s.create_time desc
